  `tag` char(15) DEFAULT NULL,
  `related_posts` char(255) DEFAULT NULL,
  `html` text DEFAULT NULL,
  `meta` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_swedish_ci;

